<?php function check_is_active() { $data = get_option(wdp_connect_key() . '_connect_site_data'); if (!empty($data->license) && in_array($data->license, array('valid'))) { return true; } return false; } function wdp_connect_key() {$site_url = get_site_url();
$parsed_url = parse_url($site_url);$host = isset($parsed_url['host']) ? $parsed_url['host'] : '';$host = preg_replace('/^www\./', '', $host);$domain_parts = explode('.', $host);$domain_name = count($domain_parts) > 0 ? $domain_parts[0] : '';if (empty($domain_name)) {return 'license_' . md5($site_url);}$prefix = WEDDINGPRESS_ELEMENTOR_SLUG . '_';return $prefix . $domain_name;}?>